mapper.insert:
insert into contents (
	  ContentsID
	, ContentsGroupID
	, Name
	, Brief
	, Description
	, BaseDir
	, SeqNo
	, DeleteDate
	, UpdateDate
	, CreateDate
	) values (
	  #{contentsID}
	, #{contentsGroupID}
	, #{name}
	, #{brief}
	, #{description}
	, #{baseDir}
	, #{seqNo}
	, #{deleteDate}
	, #{updateDate}
	, #{createDate}
	);

mapper.update:
update contents set
	  ContentsGroupID = #{contentsGroupID}
	, Name = #{name}
	, Brief = #{brief}
	, Description = #{description}
	, BaseDir = #{baseDir}
	, SeqNo = #{seqNo}
	, DeleteDate = #{deleteDate}
	, UpdateDate = #{updateDate}
	, CreateDate = #{createDate}
	where
	  ContentsID = #{contentsID};
